    read -rp "Choice / –í—ã–±–æ—Ä [1]: " _lang_choice
    case "${_lang_choice:-1}" in
        2) VWN_LANG="en" ;;
        *) VWN_LANG="ru" ;;
    esac
    mkdir -p "$(dirname "$VWN_CONF")"
    # –°–æ—Ö—Ä–∞–Ω—è–µ–º, –Ω–µ —Ç—Ä–æ–≥–∞—è –æ—Å—Ç–∞–ª—å–Ω—ã–µ –Ω–∞—Å—Ç—Ä–æ–π–∫–∏
